export function getStepsForRole(roleSystemName: string): WorkflowStep[] {
  const allSteps: WorkflowStep[] = [];
  for (const stage of workflowStages) {
    for (const step of stage.steps) {
      const allowedRoles = DEPARTMENT_ROLES[step.initiator] || [];
      if (roleSystemName === 'admin' || allowedRoles.includes(roleSystemName)) {
        allSteps.push(step);
      }
    }
  }
  return allSteps;
}

export function canUserExecuteStep(roleSystemName: string, step: WorkflowStep): boolean {
  if (roleSystemName === 'admin') return true;
  const allowedRoles = DEPARTMENT_ROLES[step.initiator] || [];
  return allowedRoles.includes(roleSystemName);
}
